typedef vector<vector<int>> Graph;

int Depth(vector<int> parents) {
  int n = SZ(parents) - 1;
  vector<int> depth(n + 1);
  int res = 0;
  RE (i, n) {
    if (parents[i]) {
      depth[i] = depth[parents[i]] + 1;
      maxi(res, depth[i]);
    } else {
      depth[i] = 1;
    }
  }
  return res;
}

int Magic(int n) {
  return n - (n & (n - 1));
}

int TreeTreedepth(vector<int> parents, vector<int>& msb) { // msb[0] == -1
  assert(msb[0] == -1);
  int n = SZ(parents) - 1;
  assert(SZ(msb) >= SZ(parents));
  vector<vector<int>> ch(n + 1);
  RE (i, n) {
    ch[parents[i]].PB(i);
  }
  int full_mask = (1 << 30) - 1;
  function<int(int)> Go = [&](int v) {
    int highest_repeating = -1;
    int or_so_far = 0;
    for (auto nei : ch[v]) {
      int mask = Go(nei);
      maxi(highest_repeating, msb[mask & or_so_far]);
      or_so_far |= mask;
    }
    int to_magic = (full_mask - ((1 << (highest_repeating + 1)) - 1)) & (full_mask - or_so_far);
    int lsb_pow = Magic(to_magic);
    debug(highest_repeating, lsb_pow, to_magic);
    int ret = (or_so_far + lsb_pow) & (full_mask - lsb_pow + 1);
    debug(v, ret);
    return ret;
  };
  int res = 0;
  for (auto root : ch[0]) {
    int mask = Go(root);
    maxi(res, msb[mask]);
  }
  return res + 1;
}

