    graph[a].PB(b);
    graph[b].PB(a);
  }
  vector<int> parents(n + 1);
  function<void(int, int)> Dfs = [&](int v, int p) {
    parents[v] = p;
    for (auto nei : graph[v]) {
      if (nei == p) { continue; }
      Dfs(nei, v);
    }
  };
  Dfs(1, 0);
  vector<int> msb(n + 1);
  msb[0] = -1;
  RE (i, n) {
    msb[i] = msb[i - 1] + !(i & (i - 1));
  }
  debug(msb[3]);
  cout<<TreeTreedepth(parents, msb) - 1<<endl;

  return 0;
}

