vector<int> g[N];
bool used[N];
int par[N];
int sz[N];

bool dfs(int u, int p, int n, int c)
{
    sz[u] = 1;
    bool ok = true;
    for (int v : g[u])
        if (v != p && !used[v])
        {
            if (dfs(v, u, n, c))
                return true;
            sz[u] += sz[v];
            if (sz[v] * 2 > n)
                ok = false;
        }
    if (sz[u] * 2 < n)
        ok = false;
    if (ok)
    {
        par[u] = c;
        used[u] = true;
        for (int v : g[u])
            if (v != p && !used[v])
                dfs(v, -1, sz[v], u);
        if (p != -1 && !used[p])
            dfs(p, -1, n - sz[u], u);
    }
    return ok;
}

