int dfs(int u, int prev) {
    int value = 100;
    int bit = 0;
    for(int v : E[u]) {
        if (v == prev) continue;
        int t = dfs(v, u);
        if (bit&t) {
            value = min(value, __builtin_clz(bit&t));
        }
        bit |= t;
    }
    if (value == 100) value = 31;
    for(int i = 31 - value; i < 32; ++i) {
        if (!(bit >> i & 1)) {
            bit |= 1 << i;
            bit &= ~((1<<i) - 1);
            break;
        }
    }
    return bit;
}

