int dfs(int p, int u)
{
    int ban = 0;
    int k = 0;
    for (auto&& v : tree[u]) {
        if (v != p) {
            int ret = dfs(u, v);
            int high = 31 - (int)__builtin_clz(ban & ret);
            k = std::max(k, high);
            ban |= ret;
        }
    }
    while (ban >> k & 1) {
        k ++;
    }
    return (ban | (1 << k)) & ~((1 << k) - 1);
}

