inline int getint()
{
	static char c;
	while ((c = getchar()) < '0' || c > '9');

	int res = c - '0';
	while ((c = getchar()) >= '0' && c <= '9')
		res = res * 10 + c - '0';
	return res;
}

const int MaxN = 100000;

int n;
vector<int> adj[MaxN + 1];

int sta[MaxN + 1];
int lab[MaxN + 1];

int dfs(int u, int prev)
{
	sta[u] = 0;

	int mask = 0;
	for (int v : adj[u])
		if (v != prev)
		{
			int sv = dfs(v, u);
			mask |= sv & sta[u];
			sta[u] |= sv;
		}

	if (sta[u] == 0)
		sta[u] = 1, lab[u] = 0;
	else
	{
		int l = 0;
		while (1 << l <= mask)
			++l;
		while (sta[u] >> l & 1)
			++l;

		lab[u] = l;
		sta[u] >>= l, sta[u] <<= l;
		sta[u] |= 1 << l;
	}

	return sta[u];
}

int main()
{
	cin >> n;
	for (int i = 1; i < n; ++i)
	{
		int u = getint(), v = getint();
		adj[u].push_back(v);
		adj[v].push_back(u);
	}

	dfs(1, 0);

	cout << *max_element(lab + 1, lab + n + 1);
	cout << endl;

	return 0;
}

#include<bits/stdc++.h>

using namespace std;

const int N = 123456;

int n, root, all, size[N], w[N], dp[N];
vector<int> adj[N];
bool visit[N];

void find_root(int x, int f = 0) {
  size[x] = 1;
  w[x] = 0;
  for (auto y : adj[x]) {
    if (y != f && !visit[y]) {
      find_root(y, x);
      size[x] += size[y];
      w[x] = max(w[x], size[y]);
    }
  }
  w[x] = max(w[x], all - size[x]);
  if (w[x] < w[root]) {
    root = x;
  }
}

void solve(int x) {
  dp[x] = 0;
  visit[x] = true;
  int old = all;
  for (auto y : adj[x]) {
    if (!visit[y]) {
      if (size[y] > size[x]) {
        all = old - size[x];
      } else {
        all = size[y];
      }
      root = 0;
      find_root(y);
      int z = root;
      solve(z);
      dp[x] = max(dp[x], dp[z] + 1);
    }
  }
}

int main() {
  ios::sync_with_stdio(false);
  cin.tie(0);
  cout.tie(0);
  cin >> n;
  for (int i = 1, x, y; i < n; ++i) {
    cin >> x >> y;
    adj[x].push_back(y);
    adj[y].push_back(x);
  }
  w[0] = all = n;
  find_root(1);
  int x = root;
  solve(x);
  cout << dp[x] << '\n';
  return 0;
}

