}; 

Edge e[200005];
int head[100005];

int f[100005],g[100005],ans;

void dfs(int x,int fa) {
  int sum=0;
  for(int i=head[x];i;i=e[i].next)
    if (e[i].t!=fa) {
    	int u=e[i].t;
    	dfs(u,x);
    	g[x]|=g[u];
    	sum|=g[x]&g[u];
	}
  sum|=g[x];
  for(int i=0;i<20;i++)
    if (!(sum&(1<<i))) {
    	f[x]=i;
    	break;
	} 
    else g[x]&=((1<<20)-1)^(1<<i);
