#include <functional>
using namespace std;
int solve(int pos, int pre, vector<vector<int> > &g) {
	int req = -1, mx = 0;
	vector<int> lis;
	for (int i : g[pos]) {
		if (i == pre) continue;
		int res = solve(i, pos, g);
		mx = max(mx, res);
		req &= ~res;
		int sz = -1;
		for (int j = 0; j < 32; ++j) {
			if ((res >> j) & 1) sz = j;
		}
		lis.push_back(sz);
	}
	int lf = 0;
	if(lis.size() >= 2) {
		sort(lis.begin(), lis.end(), greater<int>());
		lf = lis[1] + 1;
	}
	for (int i = lf; i < 32; ++i) {
		if ((req >> i) & 1) return ((mx >> i) + 1) << i;
	}
	return -1;
}
int main() {
	cin.tie(0);
	ios_base::sync_with_stdio(false);
	int n, a, b;
	cin >> n;
	vector<vector<int> > g(n);
	for (int i = 0; i < n - 1; ++i) {
		cin >> a >> b; --a, --b;
		g[a].push_back(b);
		g[b].push_back(a);
	}
	int ret = solve(0, -1, g);
	int ans = 0;
	while (2 << ans <= ret) ++ans;
	cout << ans << '\n';
	return 0;
}

