int dfs(int idx, int par) {
  int ret = 0;
  for(auto &to : g[idx]) {
    if(to != par) ret = max(ret, dfs(to, idx));
  }
  for(int i = 0; i < 30; i++) {
    bool correct = true;
    for(auto &to : g[idx]) {
      if(to != par) {
        if(v[to][i]) correct = false;
      }
    }
    if(correct) {
      ret = max(ret, i);
      v[idx][i] = true;
      for(auto &to : g[idx]) {
        if(to != par) {
          for(int j = i + 1; j < 30; j++) {
            v[idx][j] |= v[to][j];
          }
        }
      }
      break;
    }
  }
  return ret;
}

