using namespace std;

const int MAXN = 1e5 + 10;
int n, mxv, mxd, par[MAXN];
vector<int> Mat[MAXN];
bool del[MAXN];

void dfs(int v, int p, int d) {
	par[v] = p;
	if (mxd < d)
		mxd = d, mxv = v;
	for (int i = 0; i < Mat[v].size(); i++)
		if (Mat[v][i] != p && !del[Mat[v][i]])
			dfs(Mat[v][i], v, d + 1);
}

int getr(int v, int d) {
	for (int i = 0; i < d / 2; i++)
		v = par[v];
	return v;
}

int getans(int v) {
	del[v] = 1;
	int mxne = 0, mxnev = -1;
	for (int i = 0; i < Mat[v].size(); i++) {
		if (del[Mat[v][i]])
			continue;
		mxd = 0, mxv = 0;
		dfs(Mat[v][i], Mat[v][i], 0);
		mxd = 0;
		dfs(mxv, mxv, 0);
		int root = getr(mxv, mxd);
		if (mxnev < mxd)
			mxnev = mxd, mxne = root;
	}
	if (mxnev == -1)
		return 0;
	return getans(mxne) + 1;
}

int main() {
	cin >> n;
	for (int i = 0; i < n - 1; i++) {
		int a, b;
		cin >> a >> b;
		Mat[a].push_back(b);
		Mat[b].push_back(a);
	}
	mxv = 1;
	dfs(1, 1, 0);
	mxd = 0;
	dfs(mxv, mxv, 0);
	int root = getr(mxv, mxd);
	cout << getans(root);
}

