const int MAXN = 100000;

vector<int> g[1 + MAXN];
bool marked[1 + MAXN];
int weight[1 + MAXN], dad[1 + MAXN], nodes[1 + MAXN];

void DFS(int node, int father) {
    nodes[0]++;
    nodes[nodes[0]] = node;
    weight[node] = 1;
    dad[node] = father;
    for (auto &son : g[node])
        if (son != father && !marked[son]) {
            DFS(son, node);
            weight[node] += weight[son];
        }
}

int Solve(int node) {
    nodes[0] = 0;
    DFS(node, 0);
    if (nodes[0] == 1)
        return 0;
    int centroid = 0;
    for (int i = 1; i <= nodes[0]; i++) {
        int now = nodes[i], biggest;
        biggest = nodes[0] - weight[now];
        for (auto &son : g[now])
            if (son != dad[now] && !marked[son])
                biggest = max(biggest, weight[son]);
        if (biggest <= nodes[0] / 2) {
            centroid = now;
            break;
        }
    }
    marked[centroid] = true;
    int answer = 0;
    for (auto &son : g[centroid])
        if (!marked[son])
            answer = max(answer, Solve(son));
    return answer + 1;
}

int main() {
    int n;
    cin >> n;
    for (int i = 1; i < n; i++) {
        int a, b;
        cin >> a >> b;
        g[a].push_back(b);
        g[b].push_back(a);
    }
    cout << Solve(1) << "\n";
    return 0;
}

