const int N = 100 * 1000 + 10;

vector<int> g[N];
bool del[N];
int size[N];
int max_s[N];
vector<int> cur;
int ans = 0;

void dfs(int v, int par) {
    size[v] = 1;
    max_s[v] = 0;
    cur.push_back(v);
    for (int to : g[v])
        if (!del[to] && to != par) {
            dfs(to, v);
            size[v] += size[to];
            max_s[v] = max(max_s[v], size[to]);
        }
}

void build(int st, int curh) {
    ans = max(ans, curh);
    cur.clear();
    dfs(st, st);
    if (cur.size() == 1)
        return;
    int v = -1;
    int tot = cur.size();
    for (int x : cur)
        if (max_s[x] * 2 <= tot && (tot - size[x]) * 2 <= tot)
            v = x;
    assert (v != -1);
    del[v] = true;
    for (int to : g[v])
        if (!del[to])
            build(to, curh + 1); 
}


