int n,fst[N],to[N*2],nxt[N*2],l,fa[N],b[N],Ans;
void link(int x,int y)
{
	to[++l]=y;nxt[l]=fst[x];fst[x]=l;
	to[++l]=x;nxt[l]=fst[y];fst[y]=l;
}
void dfs(int x)
{
	int tmp=0;
	for (int i=fst[x];i;i=nxt[i])
	if (to[i]!=fa[x])
	{
		fa[to[i]]=x;
		dfs(to[i]);
		tmp|=b[to[i]];
	}
	int t=((~tmp)^((-1<<31)))&(-((~tmp)^((-1<<31))));
	//cout<<t<<endl;
	b[x]=tmp^t^(tmp&(t-1));
	Ans=max(Ans,t);
}
