	for (int i=1;i<n;i++)
	{
		int x,y;
		scanf("%d%d",&x,&y);
		link(x,y);
	}
	dfs(1);
	int ans=0;
	while(Ans>1)Ans>>=1,ans++;
	printf("%d\n",ans);
}

