    if(1){
      vector<Int> used(n,0);
      queue<Int> q({0});
      used[0]=1;
      while(!q.empty()){
	Int v=q.front();q.pop();
	if(v<0||v>=n) exit(0);
	for(Int u:G[v]){
	  if(u<0||u>=n) exit(0);
	  if(used[u]) continue;
	  used[u]=1;
	  q.emplace(u);
	}
      }
      for(Int i=0;i<n;i++) assert(used[i]);
    }

    vector<Int> cnt(n);
    queue<Int> q;
    for(Int i=0;i<n;i++){
      cnt[i]=G[i].size();
      if(cnt[i]==1) q.emplace(i);
    }
    //break;

    vector<Int> used(n,0);
    vector<Int> belong(n,-1);
    vector<vector<Int> > T;

    auto disable=[&](Int v){
      //cout<<"d1:"<<v<<endl;
      for(Int u:G[v]){
	if(used[u]) continue;
	cnt[u]--;
	if(cnt[u]==1) q.emplace(u);
      }
    };

    auto disable2=[&](Int v){
      //cout<<"d2:"<<v<<endl;
      for(Int u:G[v]){
	if(used[u]) continue;
        used[u]=2;
	belong[u]=T.size();
	T.emplace_back();
	for(Int x:G[u]){	  
	  if(used[x]) continue;
	  cnt[x]--;
	  if(cnt[x]==1) q.emplace(x);
	}
      }
    };

