      Int v=q.front();q.pop();
      if(used[v]) continue;
      Int t=T.size();
      T.emplace_back();

      Int c=-1;      
      for(Int u:G[v])
	if(!used[u]) c=u;

      if(c<0){
	used[v]=1;
	belong[v]=t;
	continue;
      }

      disable(c);
      used[c]=3;
      belong[c]=t;

      for(Int u:G[c]){
	if(used[u]) continue;

	if(cnt[u]>1){
	  disable(u);
	  used[u]=2;
	  belong[u]=T.size();
	  T.emplace_back();
	  continue;
	}

	used[u]=1;
	belong[u]=t;
	disable2(u);
      }
    }
    //break;

    //for(Int i=0;i<n;i++) cout<<i<<":"<<used[i]<<" "<<belong[i]<<endl;
    for(Int i=0;i<n;i++){
      if(!used[i]) exit(0);
      if(belong[i]<0) exit(0);
      if(belong[i]>=(Int)T.size()) exit(0);
    }

    for(Int v=0;v<n;v++)
      for(Int u:G[v])
	if(belong[v]!=belong[u])
	  T[belong[v]].emplace_back(belong[u]);

    for(auto &v:T){
      sort(v.begin(),v.end());
      v.erase(unique(v.begin(),v.end()),v.end());
    }

    swap(G,T);
    //break;
  }
  cout<<ans<<endl;
  return 0;
}

