		queue<int> q2;
		while(!q.empty()){
			int u = q.front(); q.pop();
			for(auto v: g[u]){
				d[v]++;
				if(g[v].size()-d[v]==1) q2.push(v);
			}
		}
		q.swap(q2);
		cnt++;
	}
	int res = 0;
	while(cnt){
		cnt >>= 1;
		res++;
	}
	cout<<res<<endl;
	return 0;
}

