int getMaxBit(int x) {
	for (int i = 30; i >= 0; i--) {
		if (x & (1 << i)) {
			return i;
		}
	}
	return -1;
}

int solve(int cur, int par = -1) {
	int msk = 0;
	int doubleMask = 0;
	for (auto to : adj[cur]) {
		if (to == par)
			continue;
		int ch = solve(to, cur);
		doubleMask |= (ch & msk);
		msk |= ch;
	}
	if (doubleMask > 0) {
		msk |= getMaxBit(doubleMask << 1) - 1;
	}
	++msk;
	res = max(res, msk);
	return msk;
}

