inline void push(int i, int j)
{ edge[++top] = (node) {j, head[i]}, head[i] = top; }

int n, a, b;
int vis[MAXN], depth[MAXN], fa[MAXN];
int len = 0, pos = 0;

void dfs_len(int nd, int f)
{
	fa[nd] = f;
	if (depth[nd] > len) len = depth[nd], pos = nd;
	for (int i = head[nd]; i; i = edge[i].next) {
		int to = edge[i].to;
		if (to == f || vis[to]) continue;
		depth[to] = depth[nd]+1;
		dfs_len(to, nd);
	}
}

int find_center(int nd)
{
	len = 0, pos = 0, depth[nd] = 0, dfs_len(nd, 0);
	int cur = pos;
	len = 0, pos = 0, depth[cur] = 0, dfs_len(cur, 0);
	for (int i = 1; i <= len/2; i++)
		pos = fa[pos];
	return pos;
}

int dfs(int nd)
{
	nd = find_center(nd);
	if (nd == 0) return 0;
	vis[nd] = 1;
	int ans = 0;
	for (int i = head[nd]; i; i = edge[i].next) {
		int to = edge[i].to;
		if (vis[to]) continue;
		ans = max(ans, dfs(to)+1);
	}
	return ans;
}

