int getv(int a)
{
    if(a==1) return 0;
    return getv(a/2) + 1;
}
void dfs(vector<set<int> >& graph, int a, int pa, bool fillflag, vector<bool>& rm)
{
    if(graph[a].size() >= 3) fillflag = false;
    rm[a] = fillflag;
    for(auto x: graph[a])
    {
        if(x==pa) continue;
        dfs(graph, x, a, !fillflag, rm);
    }
}
int solve(vector<set<int> > graph)
{
    if(graph.size() == 1) return 0;
    if(graph.size() <= 3) return 1;
    int degover2 = 0;
    for(int i=0; i<graph.size(); ++i)
    {
        if(graph[i].size() > 2) degover2 = i;
    }
    if(degover2 == 0)
    {
        return getv(graph.size());
    }
    vector<bool> rm(graph.size(), false);
    dfs(graph, degover2, -1, false, rm);


    int tp = 0;
    vector<int> acc(graph.size(), -1);
    vector<int> invacc;
    for(int i=0; i<graph.size(); ++i)
        if(!rm[i])
        {
            acc[i] = tp++;
            invacc.push_back(i);
        }
    for(int i=0; i<graph.size(); ++i)
    {
        if(!rm[i]) continue;
        assert(graph[i].size() == 1 || graph[i].size() == 2);
        if(graph[i].size() == 1)
        {
            int t = *graph[i].begin();
            graph[t].erase(i);
        }
        else if(graph[i].size() == 2)
        {
            int t = *graph[i].begin();
            int u = *graph[i].rbegin();
            graph[t].erase(i); graph[u].erase(i);
            graph[t].insert(u); graph[u].insert(t);
        }
    }
    vector<set<int> > newgraph(tp);
    for(int i=0; i<tp; ++i)
    {
        int targ = invacc[i];
        for(auto x: graph[targ])
        {
            assert(acc[x] != -1);
            newgraph[i].insert(acc[x]);
        }
    }
    return 1 + solve(newgraph);
}
int main()
{
    int N;
    scanf("%d", &N);
    vector<set<int> > graph(N);
    for(int i=0; i<N-1; ++i)
    {
        int u, v; scanf("%d%d", &u, &v);
        --u; --v;
        graph[u].insert(v);
        graph[v].insert(u);
    }
    printf("%d\n", solve(graph));
}

