#include <bits/stdc++.h>
#define F first
#define S second
#define X real()
#define Y imag()
using namespace std;
typedef long long ll;
typedef long double ld;

vector<int> g[101010];
int d[101010];
int del[101010];

int md=0;

void dfs1(int x, int p){
	d[x]=0;
	for (int nx:g[x]){
		if (del[nx]) continue;
		if (nx!=p){
			dfs1(nx, x);
			md=max(md, d[x]+d[nx]+1);
			d[x]=max(d[x], d[nx]+1);
		}
	}
}

void dfs2(int x, int p, int pd, vector<int>& cdv){
	int d1=pd;
	int d2=0;
	int d1v=0;
	for (int nx:g[x]){
		if (del[nx]) continue;
		if (nx!=p){
			if (d[nx]+1>d1){
				d2=d1;
				d1=d[nx]+1;
				d1v=nx;
			}
			else if(d[nx]+1>d2){
				d2=d[nx]+1;
			}
		}
	}
	if (d1+d2==md&&min(d1, d2)>=md/2){
		cdv.push_back(x);
	}
	for (int nx:g[x]){
		if (del[nx]) continue;
		if (nx!=p){
			if (d1v==nx){
				dfs2(nx, x, d2+1, cdv);
			}
			else{
				dfs2(nx, x, d1+1, cdv);
			}
		}
	}
}

int solve(int x){
	md=0;
	dfs1(x, 0);
	vector<int> cdv;
	if (md==0) return 0;
	if (md<=2) return 1;
	dfs2(x, 0, 0, cdv);
	assert((int)cdv.size()<=2);
	assert((int)cdv.size()>=1);
	if (cdv.size()==1){
		del[cdv[0]]=1;
		int v=0;
		for (int nx:g[cdv[0]]){
			if (!del[nx]){
				v=max(v, solve(nx)+1);
			}
		}
		del[cdv[0]]=0;
		return v;
	}
	else{
		del[cdv[0]]=1;
		int v1=solve(cdv[1])+1;
		del[cdv[0]]=0;
		del[cdv[1]]=1;
		int v2=solve(cdv[0])+1;
		del[cdv[1]]=0;
		return min(v1, v2);
	}
}

