    G[b-1].push_back(a-1);
  }

  int res = finder(G, 0, -1);
  int ret = 0;
  while (res > 1) {
    res /= 2;
    ret ++;
  }
  printf("%d\n", ret);
}

#include<bits/stdc++.h>
using namespace std;

#define ll long long
