const int maxn = 1e5 + 20;

int sz[maxn];

bool hide[maxn];

vector<int> adj[maxn];

void plant(int v , int p = -1)
{
	sz[v] = 1;
	for(auto u : adj[v])
		if(!hide[u] && u != p)
		{
			plant(u , v);
			sz[v] += sz[u];
		}
}

int find_centroid(int v , int n , int p = -1)
{
	for(auto u : adj[v])
		if(!hide[u] && u != p && 2 * sz[u] > n)
			return find_centroid(u , n , v);

	return v;
}

int solve(int v)
{
	plant(v);
	v = find_centroid(v , sz[v]);
	hide[v] = 1;

	int mx = -1;
	for(auto u : adj[v])
		if(!hide[u])
			mx = max(mx , solve(u));

	return mx + 1;
}

