int dfs(int p, int r)
{
	int k, f1, f2;

	f1 = f2 = 0;

	for(int &t: V[p]){
		if(t != r){
			k = dfs(t, p);
			f2 |= (f1 & k);
			f1 |= k;
		}
	}

	return (f1 | K[f2]) + 1;
}

