vector<vector<int> > G;
vector<vector<int> > T;
vector<int> V;

void build(int a, int p) {
    for (int i = 0;i < G[a].size();i++) {
        if (G[a][i] == p) continue;
        T[a].push_back(G[a][i]);
        build(G[a][i], a);
    }
    return;
}

int cal(int n) {
    if (V[n] != 0) return V[n];
    if (T[n].size() == 0) {
        V[n] = 1;
        return 1;
    }
    int v = 0;
    int t[31];
    memset(t, 0, sizeof(t));
    for (int i = 0;i < T[n].size();i++) {
        int z = cal(T[n][i]);
        int x = 1;
        for (int j = 0;j < 31;j++) {
            if (z & x) t[j]++;
            x *= 2;
        }
    }
    int m_v = -1;
    for (int i = 0;i < 31;i++) {
        if (t[i] > 1) m_v = i;
    }
    m_v++;
    for (int i = m_v;i < 31;i++) {
        if (t[i] == 0) {
            for (int j = 0;j < i;j++) t[j] = 0;
            t[i] = 1;
            break;
        }
    }
    int sol = 0;
    for (int i = 0;i < 31;i++) {
        if (t[i]) sol += (1 << i);
    }
    V[n] = sol;
    return sol;
}

int main() {
    ios::sync_with_stdio(false);
    int N;
    cin >> N;
    G.resize(N);
    for (int i = 1;i < N;i++) {
        int a, b;
        cin >> a >> b;
        a--;
        b--;
        G[a].push_back(b);
        G[b].push_back(a);
    }
    T.resize(N);
    build(0, -1);
    V.resize(N, 0);
    int v = cal(0);
    int x = 1;
    int sol = 0;
    for (int i = 0;i < 31;i++) {
        if (v & x) sol = i;
        x *= 2;
    }
    cout << sol << endl;
    return 0;
}

