void dfs(int u, int pa) {
  int d, c;
  for (int v: e[u]) if (v != pa) dfs(v, u);
  for (d=20; ~d; --d) {
    c = 0;
    for (int v: e[u]) if (v != pa && msk[v]>>d&1) ++c;
    if (c >= 2) {
      msk[u] += (1<<(d+1));
      break;
    } else if (c == 1)
      msk[u] |= 1<<d;
  }
  if (d == -1) ++msk[u];
}

