		scanf("%d%d",&a,&b);
	}
}e[N];
int n,ans,f[N],size;
PII q[N];
bool pd[N];
vector<int> v[N];

void dfs(int x,int fa){
	f[x] = 1;
	int t;
	for(int i=0;i<v[x].size();i++) if((t=e[v[x][i]].a^e[v[x][i]].b^x)!=fa && pd[t]){
		dfs(t,x);
		f[x] += f[t];
	}
}
int dfs2(int x,int fa){
	int t,ret = 0,tmp,cnt = 0;
	bool Pd = true;
	for(int i=0;i<v[x].size();i++) if((t=e[v[x][i]].a^e[v[x][i]].b^x)!=fa && pd[t]){
		tmp = dfs2(t,x);
		if(tmp) ret = tmp;
		cnt += f[t];
		if(f[t]<<1>size) Pd = false;
	}
	if(Pd && (size-cnt-1)<<1<=size) ret = x;
	return ret;
}

int main(){
