		e[i].Input();
		v[e[i].a].push_back(i);
		v[e[i].b].push_back(i);
	}
	int head = 0,tail = 0;
	ans = 0;
	q[0] = PII(1,0);
	memset(pd,true,sizeof(pd));
	while(head<=tail){
		int t =q[head].first;
		int tmp = q[head++].second;
		//cout<<t<<' '<<tmp<<endl;
		dfs(t,0);
		size = f[t];
