		for(int i = 0;i<v[t].size();i++) if((x=e[v[t][i]].a^e[v[t][i]].b^t) && pd[x]){
			q[++tail] = PII(x,tmp+1); 
		}
		ans = max(ans,tmp);
	}
	printf("%d\n",ans);
	return 0;
}

#include <bits/stdc++.h>

using namespace std;

int Parent[500000], Dist[500000];
set<int> G[500000];

int DFS(int node, int par, int dist) {
	Parent[node] = par;
	Dist[node] = dist;

	int ret = node;
	for(auto vec : G[node])
		if(vec != par) 
			ret = max(ret, DFS(vec, node, dist + 1), [&](int a, int b) {
				return Dist[a] < Dist[b]; 
			});

	return ret;
}

int GetCenter(int node) {
	int last = DFS(node, 0, 0);
	last = DFS(last, 0, 0);

	int d = Dist[last];
	for(int i = 0; i < d / 2; ++i)
		last = Parent[last];
	return last;
}

int Decomp(int node) {
	int cen = GetCenter(node);
	for(auto vec : G[cen]) {
		G[vec].erase(cen);
	}

	int ans = 0;
	for(auto vec : G[cen])
		ans = max(ans, Decomp(vec) + 1);
	return ans;
}

