struct HASH {
	size_t operator() (const pair<int,int> &x) const {
		return hash <long long> () (((long long)x.first) ^ (((long long)x.second) << 32));
	}
};

const int MAX_N = 1e5 + 5;
int n, cnt, ans, S;
bool Mark[MAX_N];
bool del[MAX_N];
int d[MAX_N];
int down[MAX_N];
int up[MAX_N];
unordered_map <pii, int, HASH> Map;

vector <pii> G[MAX_N];

void dfs1 (int v,  int p) {
	for (int i = 0; i < G[v].size(); i++)
		if (G[v][i].second && G[v][i].first != p) {
			dfs1(G[v][i].first, v);
			down[v] += 1 + down[G[v][i].first];
		}
}

void dfs2 (int v, int p, int u) {
	Mark[v] = true;
	up[v] = u;

	for (int i = 0; i < G[v].size(); i++)
		if (G[v][i].second && G[v][i].first != p)
			dfs2(G[v][i].first, v, u + down[v] - down[G[v][i].first]);
}

void findS (int v, int p) {
	for (int i = 0; i < G[v].size(); i++)
		if (G[v][i].second && G[v][i].first != p && (up[G[v][i].first] < 1 + down[G[v][i].first] || (up[G[v][i].first] == 1 + down[G[v][i].first] && rand() % 2))) {
			findS(G[v][i].first, v);
			return ;
		}

	S = v;
}

int p = 1, l;
int dis[MAX_N];
int Arr[MAX_N];
int L, R;
vector <int> g[MAX_N];

void bfs (int v) {
	memset(dis, 127, sizeof dis);
	dis[v] = 0;
	L = R = 0;
	Arr[R++] = v;

	while (L < R) {
		int u = Arr[L++];
		for (int i = 0; i < g[u].size(); i++)
			if (1 + dis[u] < dis[g[u][i]]) {
				dis[g[u][i]] = 1 + dis[u];
				Arr[R++] = g[u][i];
			}
	}
}

int main() {
	srand(clock());
	cin >> n;
	for (int i = 1; i < n; i++) {
		int v, u;
		cin >> v >> u;
		v--; u--;
		d[v]++;
		d[u]++;

		Map[{v, u}] = G[u].size();
		Map[{u, v}] = G[v].size();
		G[v].push_back({u, 1});
		G[u].push_back({v, 1});
		g[v].push_back(u);
		g[u].push_back(v);
	}

	while (true) {
		ans++;
		memset(Mark, 0, sizeof Mark);
		memset(down, 0, sizeof down);
		memset(up, 0, sizeof up);
		bool change = false;

		for (int i = 0; i < n; i++)
			if (!del[i] && !Mark[i]) {
				change = true;

				dfs1(i, i);
				dfs2(i, i, 0);
				findS(i, i);
				del[S] = true;
				//cout << "&   " << S << "\n";
				for (int j = 0; j < G[S].size(); j++)
					if (G[S][j].second) {
						int v = G[S][j].first;
						int ind = Map[{S, v}];

						d[v]--;
						G[v][ind].second = false;
					}
			}

		for (int i = 0; i < n; i++)
			if (!d[i]) {
				//cout << "^ " << i << "\n";
				del[i] = true;
			}

		if (!change)
			break;
	}

	bfs(0);
	int k = 0;
	for (int i = 0; i < n; i++)
		if (dis[k] < dis[i])
			k = i;

	bfs(k);
	for (int i = 0; i < n; i++)
		if (ans < dis[i])
			ans = dis[i];

	ans++;

	while (p <= ans) {
		l++;
		p <<= 1;
	}

	cout << max(ans, l) - 1 << "\n";
	return 0;
}

#include<iostream>
#include<cstdio>
