using namespace std;
struct edges
{
	int to,next;
}edge[202020];
int first[101010],now;
void adde(int x,int y)
{
	now++;
	edge[now].to=y;
	edge[now].next=first[x];
	first[x]=now;
}
int s[101010],al=(1<<20)-1;
int lowbit(int x)
{
	return x&(-x);
}
void work(int p,int fa)
{
	int mk=1;
	s[p]=0;
	for(int e=first[p];e;e=edge[e].next)
		if(edge[e].to!=fa)
		{
			work(edge[e].to,p);
			mk=max(mk,lowbit(s[p]&s[edge[e].to]));
			s[p]|=s[edge[e].to];
		}
	while(mk&s[p])
		mk<<=1;
	s[p]&=(al^(mk-1));
	s[p]^=mk;
}
int main()
{
	int n,x,y,ans;
	scanf("%d",&n);
	for(int i=1;i<n;i++)
	{
		scanf("%d%d",&x,&y);
		adde(x,y);
		adde(y,x);
	}
	work(1,0);
	ans=-1;
	while(s[1])
	{
		ans++;
		s[1]>>=1;
	}
	printf("%d",ans);
	return 0;
}

#include <iostream>
