void add(int &x,int y)
{
    x += y;
    if(x >= mod) x -= mod;
    return;
}

struct BIT
{
    vi not_zero;
    int num[N];
    void init()
    {
        for(int i:not_zero) num[i] = 0;
        not_zero.clear();
        return;
    }
    int lowbit(int i){ return i & (-i); }
    void modify(int i,int x)
    {
        i++;
        for(; i <= n + 1; i += lowbit(i))
        {
            add(num[i],x);
            not_zero.pb(i);
        }
        return;
    }
    int query(int i)
    {
        i++;
        int ret = 0;
        for(; i; i ^= lowbit(i)) add(ret,num[i]);
        return ret;
    }
}X,Y;

int Find(int lb,int rb,LL x)
{
    while(lb <= rb)
    {
        int mid = lb + rb >> 1;
        if(a[mid] > x) rb = mid - 1;
        else lb = mid + 1;
    }
    return rb;
}

