}

const int Max_N(100050);

int N, Head[Max_N], Total, To[Max_N << 1], Next[Max_N << 1], Val[Max_N];

inline void Add_Edge(const int &s, const int &t)
{
	++Total, To[Total] = t, Next[Total] = Head[s], Head[s] = Total;
}

void init()
{
	Get_Val(N);
	for (int i = 1, u, v;i != N;++i)
		Get_Val(u), Get_Val(v), Add_Edge(u, v), Add_Edge(v, u);
}

int Ans;

void dfs(const int &u, const int &fa)
{
	int All(0), Lca(0);
	for (int i = Head[u], v;i;i = Next[i])
		if ((v = To[i]) != fa)
		{
			dfs(v, u);
			Lca |= (All & Val[v]);
			All |= Val[v];
		}
	for (int x = 0;;++x)
	{
		if (x - 1 >= 0)
			if (Lca & (1 << (x - 1)))
				Lca ^= (1 << (x - 1));
		if (Lca == 0 && (All & (1 << x)) == 0)
		{
			Val[u] = (All | (1 << x));
			for (int j = 0;j <= x - 1;++j)
				if (Val[u] & (1 << j))
					Val[u] ^= (1 << j);
			Ans = max(Ans, x);
			break;
		}
	}
}

