void dfs(int v, int par = -1) {
    f[v] = 0;
    forn(i, g[v].size()) {
        int to = g[v][i];
        if (to == par) continue;
        dfs(to, v);
        f[v] |= f[to];
    }
    int free_bit = 0;
    while (f[v] & (1 << free_bit)) ++free_bit;
    f[v] |= (1 << free_bit);
    forn(i, free_bit) f[v] -= (1 << i);
}

