using namespace std;
typedef pair<int,int> PII;
const int N = 100010;
struct Edge{
	int a,b;
	void Input(){
		scanf("%d%d",&a,&b);
	}
}e[N];
int n,ans,f[N],Next[N];
PII q[N];
bool pd[N];
vector<int> v[N];

void dfs(int x,int fa){
	f[x] = 0;
	Next[x] = 0;
	int t;
	for(int i=0;i<v[x].size();i++) if((t=e[v[x][i]].a^e[v[x][i]].b^x)!=fa && pd[t]){
		dfs(t,x);
		if(f[t]>f[x]) {
			f[x] = f[t];
			Next[x] = t;
		}
	}
	f[x]++;
}

int main(){
	scanf("%d",&n);
	for(int i=0;i<n-1;i++) {
		e[i].Input();
		v[e[i].a].push_back(i);
		v[e[i].b].push_back(i);
	}
	int head = 0,tail = 0;
	ans = 0;
	q[0] = PII(1,0);
	memset(pd,true,sizeof(pd));
	while(head<=tail){
		int t =q[head].first;
		int tmp = q[head++].second;
		//cout<<t<<' '<<tmp<<endl;
		dfs(t,0);
		while(Next[t]!=0) t =Next[t];
		dfs(t,0);
		int cnt = (f[t]+1)/2-1;
		while(cnt) {
			cnt--;
			t=Next[t];
		}
		pd[t] = false;
		//cout<<t<<' '<<tmp<<endl;
		int x;
		for(int i = 0;i<v[t].size();i++) if((x=e[v[t][i]].a^e[v[t][i]].b^t) && pd[x]){
			q[++tail] = PII(x,tmp+1); 
		}
		ans = max(ans,tmp);
	}
	printf("%d\n",ans);
	return 0;
}

#include <bits/stdc++.h>
using namespace std;
const int N = 1e5 + 10, lg = 19;
int mask[N], cnt[N][lg + 2], ans, n;
vector<int> gr[N];
void dfs(int v, int par = -1) {
	int rn = 0;
	for (auto u : gr[v]) {
		if (u != par) {
			dfs(u, v), mask[v] |= mask[u];
			for (int i = 0; i < lg; i++) {
				if ((mask[u] >> i) & 1)
					cnt[v][i]++;
				if (cnt[v][i] > 1)
					rn = max(rn, i + 1);
			}
		}
	}
	for (int i = rn; i < lg; i++) {
		if (!((mask[v] >> i) & 1)) {
			rn = i;
			break;
		}
	}
	ans = max(ans, rn);
	mask[v] = mask[v] & (((1 << lg) - 1) ^ ((1 << rn) - 1));
	mask[v] |= (1 << rn);
}	
int main() {
	cin >> n;
	for (int i = 0; i < n - 1; i++) {
		int x, y;
		cin >> x >> y;
		x--, y--;
		gr[x].push_back(y), gr[y].push_back(x);
	}
	dfs(0);
	cout << ans;
	return 0;
}

#include <iostream>
#include <vector>
#include <cmath>
using namespace std;

const int MAXN = 1e5 + 10;
int n, mxv, mxd, par[MAXN];
vector<int> Mat[MAXN];
bool del[MAXN];

void dfs(int v, int p, int d) {
	par[v] = p;
	if (mxd < d)
		mxd = d, mxv = v;
	for (int i = 0; i < Mat[v].size(); i++)
		if (Mat[v][i] != p && !del[Mat[v][i]])
			dfs(Mat[v][i], v, d + 1);
}

int getr(int v, int d) {
	for (int i = 0; i < d / 2; i++)
		v = par[v];
	return v;
}

int getans(int v) {
	del[v] = 1;
	int mxne = 0, mxnev = -1, mxnee = 0;
	for (int i = 0; i < Mat[v].size(); i++) {
		if (del[Mat[v][i]])
			continue;
		mxd = -1, mxv = 0;
		dfs(Mat[v][i], Mat[v][i], 0);
		mxd = -1;
		dfs(mxv, mxv, 0);
		int root = getr(mxv, mxd);
		if (mxnev < mxd)
			mxnev = mxd, mxne = root, mxnee = par[root];
	}
	if (mxnev == -1)
		return 0;
	int k = getans(mxne) + 1;
	k = min(k, getans(mxnee) + 1);
	del[v] = 0;
	return k;
}

int main() {
	cin >> n;
	for (int i = 0; i < n - 1; i++) {
		int a, b;
		cin >> a >> b;
		Mat[a].push_back(b);
		Mat[b].push_back(a);
	}
	mxv = 1, mxd = -1;
	dfs(1, 1, 0);
	mxd = -1;
	dfs(mxv, mxv, 0);
	int root = getr(mxv, mxd);
	int pt = par[root];
	if (mxd % 2 == 0)
		cout << getans(root);
	else
		cout << min(getans(root), getans(pt));
}

