using namespace std;
typedef long long LL;
const int maxn = 100005;

int ehead[maxn],ecnt;
struct edge {
	int u,v,next;
}edg[maxn<<1];
void add(int u,int v) {
	edg[++ecnt]=(edge){u,v,ehead[u]};
	ehead[u]=ecnt;
	edg[++ecnt]=(edge){v,u,ehead[v]};
	ehead[v]=ecnt;
}
int n,bit[maxn],f[maxn],ans;
void dfs(int u,int fa)
{
	int conf=0;
	for (int v,j=ehead[u];j;j=edg[j].next)
	if ((v=edg[j].v)!=fa) {
		dfs(v,u);conf|=bit[u]&bit[v];bit[u]|=bit[v];
	}
	while ((conf>>f[u])||(bit[u]>>f[u]&1)) ++f[u];
	bit[u]=(bit[u]>>f[u]<<f[u])|(1<<f[u]);
	ans=max(ans,f[u]);
}
