		freopen("agc009d.in","r",stdin);
		freopen("agc009d.out","w",stdout);
	#endif
	scanf("%d",&n);
	for (int u,v,i=1;i<n;i++)
		scanf("%d %d",&u,&v),add(u,v);
	dfs(1,0);printf("%d",ans);
	return 0;
}

