int finder(vector<vector<int> > G, int now, int prv) {
  vector<int> bits;
  for (auto nxt : G[now]) {
    if (nxt == prv) continue;
    bits.push_back(finder(G, nxt, now));
  }
  int res = 0;
  for (int bit=20; bit>=0; bit--) {
    int cnt = 0;
    for (auto p : bits) {
      if (p&(1<<bit)) cnt ++;
    }
    if (cnt == 1) res += 1<<bit;
    else if (cnt > 1) {
      res += 1<<(bit+1);
      return res;
    }
  }
  res ++;
  return res;
}

