  for (int i=0; i<N-1; i++) {
    int a, b;
    cin >> a >> b;
    G[a-1].push_back(b-1);
    G[b-1].push_back(a-1);
  }

  int res = finder(G, 0, -1);
  int ret = 0;
  while (res > 1) {
    res /= 2;
    ret ++;
  }
  printf("%d\n", ret);
}

