#include <bits/stdc++.h>

#define MAXN (100010)

int n;
int sz[MAXN];
bool vis[MAXN];
std::vector<int> mat[MAXN];

int getroot(int u, int fa, int &root, int &rootsz, int nown){
	sz[u] = 1;
	int maxsz = 0;
	for(auto v : mat[u]){
		if(vis[v] || v == fa) continue;
		getroot(v, u, root, rootsz, nown);
		sz[u] += sz[v];
		maxsz = std::max(maxsz, sz[v]);
	}
	maxsz = std::max(maxsz, nown - sz[u]);
	if(rootsz > maxsz){
		rootsz = maxsz;
		root = u;
	}
}

int solve(int u){
	vis[u] = true;
	int ret = 0;
	for(auto v : mat[u]){
		if(vis[v]) continue;
		int root = 0, rootsz = n;
		getroot(v, u, root, rootsz, sz[v]);
		ret = std::max(ret, solve(root));
	}
	return ret + 1;
}

int main(){
	scanf("%d", &n);
	for(int i = 1; i < n; ++ i){
		int u, v;
		scanf("%d%d", &u, &v);
		mat[u].push_back(v);
		mat[v].push_back(u);
	}
	int root = 0, rootsz = n;
	getroot(1, 0, root, rootsz, n);
	printf("%d\n", solve(root) - 1);

	return 0;
}

