		Map[{u, v}] = G[v].size();
		G[v].push_back({u, 1});
		G[u].push_back({v, 1});
	}

	while (true) {
		ans++;
		memset(Mark, 0, sizeof Mark);
		memset(down, 0, sizeof down);
		memset(up, 0, sizeof up);
		bool change = false;

		for (int i = 0; i < n; i++)
			if (!del[i] && !Mark[i]) {
