inline int lowbit(int i)
{ return i&(-i); }

void dfs(int nd, int f)
{
	for (int i = head[nd]; i; i = edge[i].next) {
		int to = edge[i].to;
		if (to == f) continue;
		dfs(to, nd);
		dp[nd] |= dp[to];
	}
	dp[nd]++;

}

