void add_edge(int a, int b)
{
    e[++cnt] = (Edge){ head[a], b };
    head[a] = cnt;
}

void dfs(int nod, int tata)
{
    for (int i = head[nod]; i; i = e[i].nxt) {
        int v = e[i].to;
        if (v == tata)
            continue;
        dfs(v, nod);
        for (int j(0); j <= LG; j++)
            dp[nod][j] += dp[v][j];
    }
    for (int i(LG); i >= 0; i--) {
        if (dp[nod][i] <= 1)
            continue;
        int p(i);
        while (dp[nod][p])
            p++;
        dp[nod][p] = 1;
        for (int j(0); j < p; j++)
            dp[nod][j] = 0;
        return;
    }

    int p(0);
    while (dp[nod][p])
        p++;
    dp[nod][p] = 1;
    for (int i(0); i < p; i++)
        dp[nod][i] = 0;
}

