const int MAXN = 100000 + 5;
int n;
vector<int> adj[MAXN];
int del[MAXN];
int size[MAXN];
void dfs(int u, int p) {
	size[u] = 1;
	for (int i = 0; i < adj[u].size(); i++) {
		int v = adj[u][i];
		if (v != p && !del[v]) {
			dfs(v, u);
			size[u] += size[v];
		}
	}
}
int findcentroid(int u) {
	int p = -1; dfs(u, -1);
	int cap = size[u] >> 1;
	while (1) {
		bool found = false;
		for (int i = 0; i < adj[u].size(); i++) {
			int v = adj[u][i];
			if (v != p && !del[v] && size[v] > cap) {
				found = true;
				p = u; u = v;
				break;
			}
		}
		if (!found) return u;
	}
}
int divide(int u) {
	u = findcentroid(u);
	del[u] = 1;
	int res = 0;
	for (int i = 0; i < adj[u].size(); i++) {
		int v = adj[u][i];
		if (!del[v]) {
			res |= divide(v);
		}
	}
	FOR(i, 0, 30) if (!bit(res, i)) {
		FOR(j, 0, i) res |= 1 << j, res ^= 1 << j;
		res |= 1 << i;
		break;
	}
	return res;
}

