vector<pii> a[K];
int n;

void dfs(int v, int par)
{
    int d = 0;
    for (int u : g[v])
    {
        if (u == par) continue;
        dfs(u, v);
        d = max(d, dp[u][0].second);
    }
    for (int i = 0; i < K; i++)
        a[i].clear();
    for (int u : g[v])
        if (u != par)
            a[dp[u][0].second].push_back(mp(u, 0));
//    printf("solving %d\n", v);
    int goodLen = 0;
    int goodD = d + 1;
    while(true)
    {
//        printf("d = %d\n", d);
        if (d < 0)
        {
            break;
        }
        if (a[d].empty())
        {
            while(d > 0 && a[d - 1].empty()) d--;
            goodLen = dp[v].size();
            goodD = d;
            d--;
            continue;
        }
        if ((int)a[d].size() >= 2)
        {
            break;
        }
        int u = a[d][0].first;
        int p = a[d][0].second;
//        printf("%d %d\n", u, p);
        dp[v].push_back(dp[u][p]);
        p++;
        if (p < (int)dp[u].size())
            a[dp[u][p].second].push_back(mp(u, p));
        d--;
    }
    while((int)dp[v].size() > goodLen) dp[v].pop_back();
    dp[v].push_back(mp(v, goodD));
    return;
}

