int getMaxK(int u, int prevP)
{
    if (u != 1 && adjList[u].size() == 1) return 1;
    vector <int> labels;
    for (int v : adjList[u])
    {
        if (prevP == v) continue;
        labels.push_back(getMaxK(v, u));
    }

    #ifdef DEBUG
    printf("%d:", u);
    for (int label : labels)
    {
        printf(" %s", bitset<3>(label).to_string().c_str());
    }
    printf("\n");
    #endif // DEBUG

    bool found = false;
    int closestZero;
    for (int k = 20; k >= 0 && !found; k--)
    {
        bool allZero = true;
        bool hasOne = false;
        for (int label : labels)
        {
            if (label & (1 << k))
            {
                allZero = false;
                if (hasOne)
                {
                    found = true;
                    break;
                }
                else
                {
                    hasOne = true;
                }
            }
        }
        if (allZero) closestZero = k;
    }
    k = max(k, closestZero);
    int resLabel = 0;
    for (int label : labels) resLabel |= label;
    resLabel &= ~((1 << closestZero) - 1);
    resLabel |= (1 << closestZero);
    return resLabel;
}

