void Link(int x,int y){
	E[cnt]=(Edge){y,las[x]};las[x]=cnt++;
	E[cnt]=(Edge){x,las[y]};las[y]=cnt++;
}
void dfs(int x,int d){
	if (d>mx) mx=d,w=x;
	for (int i=las[x],y;~i;i=E[i].nxt)
		if (!vis[y=E[i].y]&&y!=fa[x]){
			fa[y]=x;
			dfs(y,d+1);
		}
}
int cal(int x){
	int ans=0;
	vis[x]=1;
	for (int i=las[x],y;~i;i=E[i].nxt)
		if (!vis[y=E[i].y]){
			mx=-1;fa[y]=0;
			dfs(y,0);
			mx=-1;fa[w]=0;
			dfs(w,0);
			ans=max(ans,mx);
		}
	vis[x]=0;
	return ans;
}
void solve(int x,int dep){
	ans=max(ans,dep);
	mx=-1;fa[x]=0;
	dfs(x,0);
	mx=-1;fa[w]=0;
	dfs(w,0);
	x=w;
	For(i,0,mx/2) x=fa[x];
	int u=fa[x];
	if (mx%2==1&&cal(u)<cal(x)) x=u;
	vis[x]=1;
	for (int i=las[x],y;~i;i=E[i].nxt)
		if (!vis[y=E[i].y]){
			solve(y,dep+1);
		}
}

