using namespace std;

typedef long long i64;

const int NMAX = 100005;
const int KMAX = 30;

vector<int> G[NMAX];

array<int, KMAX> dfs(int node, int prev) {
    array<int, KMAX> ret;
    fill(ret.begin(), ret.end(), 0);
    ret[0] = 1;
    for (int to: G[node]) {
        if (to != prev) {
            array<int, KMAX> son = dfs(to, node);
            for (int i = 0; i < KMAX; ++i) {
                ret[i] += son[i];
            }
        }
    }
    for (int i = 0; i < KMAX; ++i) {
        if (ret[i] > 1) {
            ret[i] = 0;
            ret[i + 1]++;
        }
    }
    return ret;
}

