	int n;
	scanf("%d", &n);
	for (int i = 0, u, v; i < n - 1; ++ i){
		scanf("%d%d", &u, &v);
		e[u].push_back(v);
		e[v].push_back(u);
	}
	dfs(1);
	int max = 0;
	for (int i = 1; i <= n; ++ i){
		max = std::max(max, ans[i]);
	}
	printf("%d\n", max);
	return 0;
}

