void solve() {
	cin >> N;
	rep(i, 0, N - 1) {
		int a, b; cin >> a >> b;
		a--; b--;
		G[a].pb(b);
		G[b].pb(a);
		cnt[a]++;
		cnt[b]++;
	}
	queue<int> que;
	rep(i, 0, N) {
		if(cnt[i] == 1) que.push(i);
	}
	int ans = 0;
	while(!que.empty()) {
		int v = que.front(); que.pop();
		int at = 0;
		rep(i, 0, sz(G[v])) {
			int n = G[v][i];
			if(cnt[n] != 0) continue;
			if(sz(S[n]) > sz(S[v])) swap(S[n], S[v]);
			for(auto d : S[n]) {
				if(S[v].count(d)) {
					while((*(S[v].begin())) != d) S[v].erase(S[v].begin());
					at = d;
				}
				else S[v].insert(d);
			}
		}
		while(!S[v].empty() && (*(S[v].begin())) == at) {
			S[v].erase(S[v].begin());
			at++;
		}
		S[v].insert(at);
		MAX(ans, at);

		// debug(v, vi(all(S[v])));

		rep(i, 0, sz(G[v])) {
			int n = G[v][i];
			if(cnt[n] == 0) continue;
			cnt[n]--;
			cnt[v]--;
			if(cnt[n] == 1) que.push(n);
		}
	}
	cout << ans << "\n";
}

